import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()
    this.handleButtonOne = this.handleButtonOne.bind(this);
    this.handleButtonTwo = this.handleButtonTwo.bind(this);
    this.handleButtonThree = this.handleButtonThree.bind(this);
    this.state = {
      clicksBtnOne: 0,
      clicksBtnTwo: 0,
      clicksBtnThree: 0,
    }
  }

  handleButtonOne() {
    this.setState(({ clicksBtnOne }) => ({
      clicksBtnOne: clicksBtnOne + 1,
    }))
  }

  handleButtonTwo() {
    this.setState(({ clicksBtnTwo }) => ({
      clicksBtnTwo: clicksBtnTwo + 1,
    }))
  }

  handleButtonThree() {
    this.setState(({ clicksBtnThree }) => ({
      clicksBtnThree: clicksBtnThree + 1,
    }))
  }

  render() {
    return (
    <>
      <button onClick={this.handleButtonOne}>{this.state.clicksBtnOne}</button>
      <button onClick={this.handleButtonTwo}>{this.state.clicksBtnTwo}</button>
      <button onClick={this.handleButtonThree}>{this.state.clicksBtnThree}</button>
    </>
    )
  }
}

export default App;
