import React, { Component } from 'react'
import './Form.css'

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      email: '',
      age: '',

    };
  }


  handleChange(event) {
    this.setState({
      name: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <h1>Cadastro</h1>
        <form className="form">
          <label>
            Nome
            <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
          </label>
          <br></br>
          <label>
            Email
            <input  type="email" name="email" onchange={this.handleChange} />
          </label>
          <br></br>
          <label>
            Idade
          <input
            type="number"
            name="age"
          />
          </label>
          <br></br>
          <label>
            Conte um pouco sobre vocÃª
            <br></br>
            <textarea rows="5" cols="50"
            name="about"
            />
          </label>
          <br></br>
          <label>
            Confirme os dados
            <input
            type="checkbox"
            name="vaiComparecer"
          />
          </label>
          <br></br>
          <button>Enviar</button>
        </form>
      </div>
    );
  }
}

export default Form;
