import React, { Component } from 'react'
import './Form.css'

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      name: '',
      email: '',
    };
  }


  handleChange(event) {
    this.setState({
      estadoFavorito: event.target.value,
    });
  }

  render() {
    return (
      <div>
        <h1>Cadastro</h1>
        <form className="form">
          <label>
            Nome
            <textarea name="name" value={this.state.estadoFavorito} onChange={this.handleChange} />
          </label>
          <br></br>
          <label>
            Qual a sua idade
          <input
            type="number"
            name="idade"
          />
          </label>
          <br></br>
          <label>
            Vai Comparecer
            <input
            type="checkbox"
            name="vaiComparecer"
          />
          </label>

        </form>
      </div>
    );
  }
}

export default Form;
